import React,{useState,useEffect} from 'react';
import './Blog.css'
import B1 from './B1.jpg'
import B2 from './B2.jpg'
import B3 from './B3.jpg'
import B4 from './B4.jpg'
function Blog(){
    const [bgColour, setBgColour] = useState("white")
    const [fcolor,setfcolor]=useState("rgb(8, 92, 2)")
    const [hcolor,sethcolor]=useState("rgb(8, 92, 2)")
    const _styles={
        background:`${bgColour}`,
        color:`${fcolor}`
  }   
  const styles={
       color:`${hcolor}`
  }
  function handlechange(){
    setBgColour("black")
    setfcolor("yellow")
    sethcolor("white")
  }
  function change(){
    setBgColour("white")
    setfcolor("rgb(8, 92, 2)")
    sethcolor("rgb(8, 92, 2)")
  }
  return(
        <div className="blog">
           <div className="rowimgb animate__animated animate__zoomIn">
             <div className="columnimgb">
                 <img src={B1} alt="img" />
                 <img src={B2} alt="img" />
             </div>
             <div className="columnimgb">
                 <img src={B3} alt="img" />
                 <img src={B4} alt="img"/>
             </div>
  <div className="__column" style={{marginLeft:'15px',marginRight:'10px'}}>
  <div class="card animate__animated animate__zoomIn" onMouseEnter={handlechange} onMouseLeave={change} style={_styles}>
              <div class="card-body">
                <h2 class="card-title"style={{paddingBottom:'2px'}}>It is an online blog website where u can compose and post your daily journals and blogs.</h2>
                <div class="card-text">
                <h3 class="" style={styles}>FEATURES</h3>
                <h5>COMPOSE AND POST BLOG <i class="fas fa-user-plus icons" style={styles}></i></h5>
                <h5>VIEW OTHER INTERESTING BLOGS <i class="far fa-comments icons" style={styles}></i></h5>
                <h3 class="" style={styles}>TECH USED</h3>
                <h5>HTML | CSS | NODE | MONGODB | BOOTSTRAP</h5>
                <h3 class="">PROJECT LINK  <a class="btn-outline-light btn-floating" href="https://github.com/Anush2303/BLOG_APP" role="button" style={{borderRadius:'100%'}}><i
                class="fab fa-github fa-2x"></i></a></h3>
            </div>
        </div>
    </div>
  </div>
 </div>
 </div>
);
}

export default Blog;
