import React,{useState,useEffect} from 'react';
import './Blog.css'
import B1 from './B1.jpg'
import B2 from './B2.jpg'
import B3 from './B3.jpg'
import B4 from './B4.jpg'
function Blog(){
    const [bgColour, setBgColour] = useState("white")
    const [fcolor,setfcolor]=useState("rgb(8, 92, 2)")
    const _styles={
        background:`${bgColour}`,
        color:`${fcolor}`
    }   
  function handlechange(){
    setBgColour("black")
    setfcolor("rgb(0, 255, 0)")
  }
  function change(){
    setBgColour("white")
    setfcolor("rgb(8, 92, 2)")
  }
    return (
        <div className="blog">
           <div className="row row animate__animated animate__zoomIn">
             <div className="column">
                 <img src={B1} alt="img" />
                 <img src={B2} alt="img" />
             </div>
             <div className="column">
                 <img src={B3} alt="img" />
                 <img src={B4} alt="img"/>
             </div>
           </div>
<div className="_column" style={{marginLeft:'15px',marginRight:'10px'}}>
  <div class="card animate__animated animate__zoomIn" onMouseEnter={handlechange} onMouseLeave={change} style={_styles}>
              <div class="card-body">
                <h2 class="card-title"style={{paddingBottom:'2px'}}>It is an online blog website where u can compose and post your daily journals and blogs.</h2>
                <div class="card-text">
                <h3 class="">FEATURES</h3>
                
            </div>
        </div>
    </div>
  </div>
</div>
    );
}

export default Blog;
